import { useState } from "react";

export default function Signup() {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [birthDate, setBirthDate] = useState('');
  const [education, setEducation] = useState('');
  const [contact, setContact] = useState('');
  const [email, setEmail] = useState('');
  const [firstNameError, setFirstNameError] = useState('');
  const [lastNameError, setLastNameError] = useState('');
  const [birthDateError, setBirthDateError] = useState('');
  const [educationError, setEducationError] = useState('');
  const [contactError, setContactError] = useState('');
  const [emailError, setEmailError] = useState('');

  const validateField = (fieldName, value) => {
    switch (fieldName) {
      case 'firstName':
        if (!value.trim()) {
          setFirstNameError('First name is required.');
        } else {
          setFirstNameError('');
        }
        break;
      case 'lastName':
        if (!value.trim()) {
          setLastNameError('Last name is required.');
        } else {
          setLastNameError('');
        }
        break;
      case 'birthDate':
        if (!value.trim()) {
          setBirthDateError('Birth date is required.');
        } else {
          setBirthDateError('');
        }
        break;
      case 'education':
        if (!value.trim()) {
          setEducationError('Education is required.');
        } else {
          setEducationError('');
        }
        break;
      case 'contact':
        if (!value.trim()) {
          setContactError('Contact is required.');
        } else {
          setContactError('');
        }
        break;
      case 'email':
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(value)) {
          setEmailError('Invalid email address.');
        } else {
          setEmailError('');
        }
        break;
      default:
        break;
    }
  };

  const handleInputChange = (fieldName, value) => {
    switch (fieldName) {
      case 'firstName':
        setFirstName(value);
        validateField('firstName', value);
        break;
      case 'lastName':
        setLastName(value);
        validateField('lastName', value);
        break;
      case 'birthDate':
        setBirthDate(value);
        validateField('birthDate', value);
        break;
      case 'education':
        setEducation(value);
        validateField('education', value);
        break;
      case 'contact':
        setContact(value);
        validateField('contact', value);
        break;
      case 'email':
        setEmail(value);
        validateField('email', value);
        break;
      default:
        break;
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if there are any error messages
    if (!firstNameError && !lastNameError && !birthDateError && !educationError && !contactError && !emailError) {
      console.log({
        firstName,
        lastName,
        birthDate,
        education,
        contact,
        email,
      });
    }
  };

  return (
    <div className="signup-container">
      <h2>Student Registration Form</h2>
      <form onSubmit={handleSubmit} className="signup-form">
        <div className="form-group">
          <label>
            First Name:
            <input
              type="text"
              value={firstName}
              onChange={(e) => handleInputChange('firstName', e.target.value)}
            />
            {firstNameError && <span className="error-message">{firstNameError}</span>}
          </label>
        </div>
        <div className="form-group">
          <label>
            Last Name:
            <input
              type="text"
              value={lastName}
              onChange={(e) => handleInputChange('lastName', e.target.value)}
            />
            {lastNameError && <span className="error-message">{lastNameError}</span>}
          </label>
        </div>
        <div className="form-group">
          <label>
            Birth Date:
            <input
              type="date"
              value={birthDate}
              onChange={(e) => handleInputChange('birthDate', e.target.value)}
            />
            {birthDateError && <span className="error-message">{birthDateError}</span>}
          </label>
        </div>
        <div className="form-group">
          <label>
            Education:
            <input
              type="text"
              value={education}
              onChange={(e) => handleInputChange('education', e.target.value)}
            />
            {educationError && <span className="error-message">{educationError}</span>}
          </label>
        </div>
        <div className="form-group">
          <label>
            Contact:
            <input
              type="text"
              value={contact}
              onChange={(e) => handleInputChange('contact', e.target.value)}
            />
            {contactError && <span className="error-message">{contactError}</span>}
          </label>
        </div>
        <div className="form-group">
          <label>
            Email:
            <input
              type="email"
              value={email}
              onChange={(e) => handleInputChange('email', e.target.value)}
            />
            {emailError && <span className="error-message">{emailError}</span>}
          </label>
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
