//     <div style={{ textAlign: 'center' }}>
//       <h1>Managing Accounts</h1>
      
//     </div>
//   );
// }


import React, { useState, useEffect } from 'react';

export default function ManageAccount() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Fetch users from the Spring API
    fetch("http://localhost:8080/getAllusers")
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to fetch users');
        }
        return response.json();
      })
      .then(data => {
        setUsers(data);
      })
      .catch(error => {
        console.error('Error fetching users:', error);
      });
  }, []);

  const deleteUser = (userId) => {
    // Implement delete user functionality
    fetch(`/deleteUser/${userId}`, {
      method: 'DELETE'
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Failed to delete user');
      }
      // Update users state after successful deletion
      setUsers(users.filter(user => user.uid !== userId));
    })
    .catch(error => {
      console.error('Error deleting user:', error);
    });
  };
console.log(users);
  return (
    <div>
      <h2>Manage Accounts</h2>
      <table>
        <thead>
          <tr>
            <th>User ID</th>
            <th>Username</th>
            <th>Password</th>
            <th>Role ID</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.uid}>
              <td>{user.uid}</td>
              <td>{user.uname}</td>
              <td>{user.pwd}</td>
              <td>{user.role_id.role_id}</td> {/* Modify this line */}
              <td>
                {user.role_id.role_id === 1 && <span>Admin</span>}
                {user.role_id.role_id === 2 && <span>Student</span>}
                {user.role_id.role_id === 3 && <span>Subscribed Student</span>}
                {user.role_id.role_id === 4 && <span>Expert</span>}
                <button onClick={() => deleteUser(user.uid)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
