import React, { useState, useEffect } from 'react';

const AttemptQuiz = () => {
    const [questions, setQuestions] = useState([]);
    const [selectedOptions, setSelectedOptions] = useState({});
    const [remainingTime, setRemainingTime] = useState(60);

    useEffect(() => {
        fetchQuizData();
        const timer = setInterval(() => {
            setRemainingTime(prevTime => prevTime - 1);
        }, 1000);
        return () => clearInterval(timer);
    }, []);

    const fetchQuizData = async () => {
        try {
            const response = await fetch('http://localhost:8080/viewQuiz');
            if (!response.ok) {
                throw new Error('Failed to fetch quiz data');
            }
            const data = await response.json();
            setQuestions(data);
            // Initialize selectedOptions state with default values
            const initialSelectedOptions = {};
            data.forEach(question => {
                initialSelectedOptions[question.id] = null;
            });
            setSelectedOptions(initialSelectedOptions);
        } catch (error) {
            console.error('Error fetching questions:', error);
        }
    };

    const handleOptionSelect = (questionId, optionIndex) => {
        setSelectedOptions(prevSelectedOptions => ({
            ...prevSelectedOptions,
            [questionId]: optionIndex
        }));
    };

    const handleSubmitQuiz = () => {
        // Perform submission logic here
        console.log('Submitting quiz...');
        // Show confirmation popup
        if (window.confirm('Are you sure you want to submit the quiz?')) {
            // Submit quiz data along with selected answers
            console.log('Quiz submitted with answers:', selectedOptions);
        }
    };

    return (
        <div className="container">
            <h2 className="mt-5">Attempt Quiz</h2>
            <p>Remaining Time: {remainingTime} seconds</p>
            <table className="table">
                <tbody>
                    {questions.map(question => (
                        <tr key={question.id}>
                            <td>{question.cat_id.cat_name}</td>
                            <td>{question.question_text}</td>
                            <td>{question.option1}</td>
                            <td>{question.option2}</td>
                            <td>{question.option3}</td>
                            <td>{question.option4}</td>
                            <td>
                             question.options.map((option, index) => ( 
                        
                                    <div key={index} className="form-check">
                                        <input
                                            type="radio"
                                            id={`option_${question.id}_${index}`}
                                            className="form-check-input"
                                            name={`question_${question.id}`}
                                            checked={selectedOptions[question.id] === index}
                                            onChange={() => handleOptionSelect(question.id, index)}
                                        />
                                        <label htmlFor={`option_${question.id}_${index}`} className="form-check-label">{option}</label>
                                    </div>
                                ))}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button className="btn btn-primary" onClick={handleSubmitQuiz}>Submit Quiz</button>
        </div>
    );
};

export default AttemptQuiz;
