import React, { useState, useEffect } from 'react';

const AttemptQuiz = () => {
    const [selectedOptions, setSelectedOptions] = useState([]);
    const [categories, setCategories] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('');
    const [subjects, setSubjects] = useState([]);
    const [selectedSubject, setSelectedSubject] = useState('');
    const [examStarted, setExamStarted] = useState(false);

    useEffect(() => {
        fetchCategories();
    }, []);

    useEffect(() => {
        if (selectedCategory) {
            fetchSubjects(selectedCategory);
        }
    }, [selectedCategory]);

    const fetchCategories = () => {
        fetch('http://localhost:8080/allCategories')
            .then(response => {
                if (response.ok)
                    return response.json();
                else
                    throw new Error('Failed to fetch categories');
            })
            .then(data => {
                console.log(data)
                setCategories(data);
            })
            .catch(error => {
                console.error('Error fetching categories:', error);
            });
    };

    const fetchSubjects = (category) => {
        fetch(`http://localhost:8080/allSubjects`)
            .then(response => {
                if (response.ok)
                    return response.json();
                else
                    throw new Error('Failed to fetch subjects');
            })
            .then(data => {
                setSubjects(data);
            })
            .catch(error => {
                console.error('Error fetching subjects:', error);
            });
    };

    const handleCategoryChange = (event) => {
        setSelectedCategory(event.target.value);
    };

    const handleSubjectChange = (event) => {
        setSelectedSubject(event.target.value);
    };

    const handleStartExam = () => {
        // Save data to database
        saveDataToDatabase();
    };

    const saveDataToDatabase = () => {
        // Perform API call to save data to database
        const data = {
            student_id: 1,
            subject_id: selectedSubject,
            cat_id: selectedCategory,
            attempted_datetime: new Date().toISOString()
        };

        fetch('http://localhost:8080/saveExamData', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(data),
        })
        .then(response => {
            if (response.ok) {
                console.log('Data saved to database successfully');
                // Navigate to StartQuiz component
                na('/startQuiz');
            } else {
                throw new Error('Failed to save data to database');
            }
        })
        .catch(error => {
            console.error('Error saving data to database:', error);
        });
    };

    return (
        <div>
            <h2>Attempt Quiz</h2>
            <div>
                <label htmlFor="category">Select Category:</label>
                <select id="category" value={selectedCategory} onChange={handleCategoryChange}>
                    <option value="">--Select Category--</option>
                    {categories && categories.map(category => (
                        <option key={category.cat_id} value={category.cat_id}>{category.cat_name}</option>
                    ))}
                </select>
            </div>  
            <div>
                <label htmlFor="subject">Select Subject:</label>
                <select id="subject" value={selectedSubject} onChange={handleSubjectChange}>
                    <option value="">--Select Subject--</option>
                    {subjects && subjects.map(subject => (
                        <option key={subject.subject_id} value={subject.subject_id}>{subject.subject_name}</option>
                    ))}
                </select>
            </div>
            {!examStarted && (
                <button onClick={handleStartExam}>Start Exam</button>
            )}
        </div>
    );
};

export default AttemptQuiz;
