import React, { useState, useEffect } from 'react';

const StartQuiz = ({ exam }) => {
    const [quizData, setQuizData] = useState([]);
    const [selectedAnswers, setSelectedAnswers] = useState({});
    const [markedForReview, setMarkedForReview] = useState({});

    useEffect(() => {
        fetchQuizData();
    }, []);
    const exam1 = JSON.parse(localStorage.getItem("current_exam"));

    const fetchQuizData = () => {
        fetch(`http://localhost:8080/viewQuizBy?cat_id=${exam1.cat_id.cat_id}&subject_id=${exam1.subject_id.subject_id}`)
            .then(resp => {
                if (!resp.ok) throw new Error("Server error");
                return resp.json();
            })
            .then(data => {
                setQuizData(data);
            })
            .catch(error => {
                console.error('Error fetching quiz questions:', error);
            });
    };

    const displayQuestions = () => {
        return quizData.map((question, index) => (
            <div key={question._id}>
                <p>{question.question}</p>
                {question.options.map((option, optionIndex) => (
                    <div key={optionIndex}>
                        <input
                            type="radio"
                            name={`question_${index}`}
                            value={option}
                            checked={selectedAnswers[`question_${index}`] === option}
                            onChange={() => handleAnswerChange(index, option)}
                        />
                        <label>{option}</label>
                    </div>
                ))}
            </div>
        ));
    };

    const handleAnswerChange = (questionIndex, option) => {
        setSelectedAnswers({
            ...selectedAnswers,
            [`question_${questionIndex}`]: option
        });
    };

    const saveStudentAnswers = () => {
        const studentAnswers = {
            exam_id: exam.exam_id,
            student_id: exam.student_id,
            answers: selectedAnswers
        };
        fetch('http://localhost:8080/saveStudentAnswers', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(studentAnswers)
        })
        .then(response => {
            if (response.ok) {
                alert('Student answers saved successfully.');
            } else {
                throw new Error('Failed to save student answers.');
            }
        })
        .catch(error => {
            console.error('Error saving student answers:', error);
            alert('Failed to save student answers. Please try again later.');
        });
    };

    const clearSelectedAnswers = () => {
        setSelectedAnswers({});
        alert('Answers cleared successfully.');
    };

    const markForReview = () => {
        // Implement marking question for review
    };

    return (
        <div>
            <h1>Start Quiz</h1>
            <div>
                {quizData.length > 0 ? displayQuestions() : <p>Loading...</p>}
            </div>
            <button onClick={saveStudentAnswers}>Save Student Answers</button>
            <button onClick={clearSelectedAnswers}>Clear</button>
            <button onClick={markForReview}>Mark for Review</button>
        </div>
    );
};

export default StartQuiz;
