import React, { useState } from "react";
import { useHistory } from "react-router-dom";

export default function Signup() {
  const history = useHistory();

  const initialFormData = {
    firstName: '',
    lastName: '',
    birthDate: '',
    education: '',
    contact: '',
    email: '',
    uname: '',
    pwd: '',
    subscription: false,
    upiId: '',
    paymentDate: '',
    amountToPay: ''
  };

  const [formData, setFormData] = useState(initialFormData);
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    let error = '';

    if (name === 'firstName' || name === 'lastName') {
      if (/\d/.test(value)) {
        error = 'Name must not contain numbers.';
      }
    } else if (name === 'contact') {
      if (!/^\d*$/.test(value)) {
        error = 'Contact must contain only digits.';
      } else if (value.length > 10) {
        error = 'Contact must not exceed 10 digits.';
      }
    }

    if (error === '') {
      setFormData(prevState => ({
        ...prevState,
        [name]: type === 'checkbox' ? checked : value
      }));
    }

    setErrors(prevErrors => ({
      ...prevErrors,
      [name]: error
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const validationErrors = {};
    for (const key in formData) {
      if (formData[key].trim() === '' && key !== 'subscription') {
        validationErrors[key] = `${key.charAt(0).toUpperCase() + key.slice(1)} is required.`;
      }
    }

    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
    } else {
      try {
        const response = await fetch("http://localhost:8080/studentRegistration", {
          method: "POST",
          headers: {
            "Content-Type": "application/json"
          },
          body: JSON.stringify(formData)
        });

        if (response.ok) {
          // Navigate to success page or handle success case
          history.push("/success"); // Change "/success" to your desired success route
        } else {
          console.error("Failed to submit form:", response.statusText);
        }
      } catch (error) {
        console.error("Error submitting form:", error);
      }
    }
  };

  const handleReset = () => {
    setFormData(initialFormData);
    setErrors({});
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <h2 className="text-center mb-4">Student Registration Form</h2>
          <form onSubmit={handleSubmit}>
            {/* Form fields */}
            {/* Add your form fields here */}
            <div className="text-center">
              <button type="submit" className="btn btn-primary mr-2">Submit</button>
              <button type="button" className="btn btn-primary mr-2" onClick={handleReset}>Reset</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
