import { Link } from 'react-router-dom';

export default function ExpertHome() {
  useEffect(() => {
    const loginid= JSON.parse(localStorage.getItem("name")).login_id;
       fetch("http://localhost:8080/getExpertById?expert_id="+loginid)
       .then(resp=>resp.json())
       .then(obj=>{
         localStorage.setItem("loggedExpert",JSON.stringify(obj))
         setExpert(obj);
       })
   }, []);
  return (
    <div style={{ textAlign: 'center' }}>
      <h1>Welcome {expert&&expert.fname}</h1>
      <div style={{ marginBottom: '20px' }}>
        <Link to="/expertHome/QuizzesandExplanations" style={{ marginRight: '20px' }}>Quizes and explanation</Link>
        <Link to="/expertHome/updateAccount" style={{ marginRight: '20px' }}>Update Account</Link>
        
      </div>
    </div>
  );
}
