import { useEffect, useState } from "react";

const StartQuiz = () => {
    const [quizData, setQuizData] = useState([]);
    const [selectedAnswers, setSelectedAnswers] = useState([]);
    const [markedForReview, setMarkedForReview] = useState([]);
    const [examStarted, setExamStarted] = useState(true);

    useEffect(() => {
        fetchQuizData();
    }, []);
    const exam = JSON.parse(localStorage.getItem("current_exam"));

    const fetchQuizData = () => {
        fetch(`http://localhost:8080/viewQuizBy?cat_id=${exam.cat_id.cat_id}&subject_id=${exam.subject_id.subject_id}`)
            .then(resp => {
                if (resp.ok)
                    return resp.json();
                else
                    throw new Error("Server error");
            })
            .then(data => {
                setQuizData(data);
                setSelectedAnswers(Array(data.length).fill({ selectedOption: null }));
                setMarkedForReview(Array(data.length).fill(false));
            })
            .catch(error => {
                console.error('Error fetching quiz questions:', error);
            });
    };

    const handleOptionSelect = (index, optionIndex) => {
        const adjustedOptionIndex = optionIndex + 1;
        const updatedSelectedAnswers = [...selectedAnswers];
        updatedSelectedAnswers[index] = { ...updatedSelectedAnswers[index], selectedOption: adjustedOptionIndex };
        setSelectedAnswers(updatedSelectedAnswers);
    };

    const handleReset = (index) => {
        const updatedSelectedAnswers = [...selectedAnswers];
        updatedSelectedAnswers[index] = { ...updatedSelectedAnswers[index], selectedOption: null };
        setSelectedAnswers(updatedSelectedAnswers);
    };

    const handleMarkForReview = (index) => {
        const updatedMarkedForReview = [...markedForReview];
        updatedMarkedForReview[index] = !markedForReview[index];
        setMarkedForReview(updatedMarkedForReview);
    };

    const handleSubmitQuiz = () => {
        // Submit all saved answers to the Spring API
        fetch('http://localhost:8080/saveStudentAnswers', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(selectedAnswers.map((answer, index) => ({
                exam_id: exam.exam_id,
                qid: quizData[index].qid, 
                student_answer: answer.selectedOption || 0
            })))
        })
            .then(response => {
                if (response.ok) {
                    console.log('Student answers submitted successfully.');
                } else {
                    console.error('Failed to submit student answers.');
                }
            })
            .catch(error => {
                console.error('Error submitting student answers:', error);
            });
    };

    return (
        <div className="container">
            {examStarted && (
                <div>
                    <table className="table">
                        <thead>
                            <tr>
                                <th>#</th>
                                <th>Question</th>
                                <th>Options</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {quizData && quizData.map((question, index) => (
                                <tr key={question.qid} className={`${markedForReview[index] ? 'marked-for-review' : ''}`}>
                                    <td>{index + 1}</td>
                                    <td>{question.question_text}</td>
                                    <td>
                                        <ul>
                                            <li>
                                                <input
                                                    type="radio"
                                                    id={`option_${index}_1`}
                                                    name={`question_${index}`}
                                                    value={question.option1}
                                                    checked={selectedAnswers[index].selectedOption === 1}
                                                    onChange={() => handleOptionSelect(index, 0)}
                                                />
                                                <label htmlFor={`option_${index}_1`}>{question.option1}</label>
                                            </li>
                                            <li>
                                                <input
                                                    type="radio"
                                                    id={`option_${index}_2`}
                                                    name={`question_${index}`}
                                                    value={question.option2}
                                                    checked={selectedAnswers[index].selectedOption === 2}
                                                    onChange={() => handleOptionSelect(index, 1)}
                                                />
                                                <label htmlFor={`option_${index}_2`}>{question.option2}</label>
                                            </li>
                                            <li>
                                                <input
                                                    type="radio"
                                                    id={`option_${index}_3`}
                                                    name={`question_${index}`}
                                                    value={question.option3}
                                                    checked={selectedAnswers[index].selectedOption === 3}
                                                    onChange={() => handleOptionSelect(index, 2)}
                                                />
                                                <label htmlFor={`option_${index}_3`}>{question.option3}</label>
                                            </li>
                                            <li>
                                                <input
                                                    type="radio"
                                                    id={`option_${index}_4`}
                                                    name={`question_${index}`}
                                                    value={question.option4}
                                                    checked={selectedAnswers[index].selectedOption === 4}
                                                    onChange={() => handleOptionSelect(index, 3)}
                                                />
                                                <label htmlFor={`option_${index}_4`}>{question.option4}</label>
                                            </li>
                                        </ul>
                                    </td>
                                    <td>
                                        <button className="btn btn-secondary" onClick={() => handleReset(index)}>Reset</button>
                                        <button className="btn btn-warning" onClick={() => handleMarkForReview(index)}>Mark for Review</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <div className="row mt-3">
                        <div className="col">
                            <button className="btn btn-success" onClick={handleSubmitQuiz}>Submit Quiz</button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default StartQuiz;
