import React, { useReducer } from "react";
import { FaEye, FaEyeSlash } from "react-icons/fa";

const initialState = {
  formData: {
    uname: "",
    pwd: "",
  },
  errors: {
    uname: "",
    pwd: "",
    loginError: "",
  },
};

const reducer = (state, action) => {
  switch (action.type) {
    case "SET_FORM_DATA":
      return {
        ...state,
        formData: {
          ...state.formData,
          [action.field]: action.value,
        },
        errors: {
          ...state.errors,
          [action.field]: "",
          loginError: "",
        },
      };
    case "SET_ERRORS":
      return {
        ...state,
        errors: {
          ...state.errors,
          ...action.errors,
        },
      };
    default:
      return state;
  }
};

const Login = () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const { formData, errors } = state;
  const [showPassword, setShowPassword] = React.useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    dispatch({ type: "SET_FORM_DATA", field: name, value });
  };

  const handleTogglePassword = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form fields
    let valid = true;
    if (formData.uname.trim() === "") {
      dispatch({
        type: "SET_ERRORS",
        errors: { uname: "Username is required." },
      });
      valid = false;
    }
    if (formData.pwd.trim() === "") {
      dispatch({
        type: "SET_ERRORS",
        errors: { pwd: "Password is required." },
      });
      valid = false;
    }

    if (valid) {
      try {
        const response = await fetch("http://localhost:8080/checkUser", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(formData),
        });

        if (response.ok) {
          console.log("Login successful");
          window.location.assign("/adminHome");
        } else {
          console.error("Login failed");
          dispatch({
            type: "SET_ERRORS",
            errors: { loginError: "Invalid username or password." },
          });
        }
      } catch (error) {
        console.error("Error logging in:", error);
        // Handle error
      }

      // Reset form data
      dispatch({
        type: "SET_FORM_DATA",
        field: "uname",
        value: "",
      });
      dispatch({
        type: "SET_FORM_DATA",
        field: "pwd",
        value: "",
      });
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card">
            <div className="card-body">
              <h2 className="card-title text-center mb-4">Login</h2>
              {errors.loginError && (
                <div className="alert alert-danger" role="alert">
                  {errors.loginError}
                </div>
              )}
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label className="form-label" htmlFor="uname">
                    Username:
                  </label>
                  <input
                    type="text"
                    id="uname"
                    name="uname"
                    className="form-control"
                    value={formData.uname}
                    onChange={handleChange}
                  />
                  {errors.uname && (
                    <p className="text-danger">{errors.uname}</p>
                  )}
                </div>
                <div className="mb-3">
                  <label className="form-label" htmlFor="pwd">
                    Password:
                  </label>
                  <div className="input-group">
                    <input
                      type={showPassword ? "text" : "password"}
                      id="pwd"
                      name="pwd"
                      className="form-control"
                      value={formData.pwd}
                      onChange={handleChange}
                    />
                    <span
                      className="input-group-text"
                      style={{ cursor: "pointer" }}
                      onClick={handleTogglePassword}
                    >
                      {showPassword ? <FaEyeSlash /> : <FaEye />}
                    </span>
                  </div>
                  {errors.pwd && (
                    <p className="text-danger">{errors.pwd}</p>
                  )}
                </div>
                <div className="text-center">
                  <button type="submit" className="btn btn-primary">
                    Login
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
