import React from 'react';
import { Link } from 'react-router-dom';
import { useState, useEffect } from "react";

export default function ExpertHome() {
  const [expert,setExpert]=useState(null);
  useEffect(() => {
    const loginid= JSON.parse(localStorage.getItem("name")).expert_id;
    console.log(loginid);
       fetch("http://localhost:8080/getExpertById?expert_id="+loginid)
       .then(resp=>resp.json())
       .then(obj=>{
         localStorage.setItem("loggedExpert",JSON.stringify(obj))
         setExpert(obj);
       })
   }, []);
  return (
    <div>
    <h1>Welcome Admin</h1>
    <nav className="navbar navbar-expand-sm bg-light mb-3">
      <div className="container-fluid">
        <ul className="navbar-nav">
          <li className="nav-item">
            <Link to="expertRegistration" className="nav-link px-3">Create Expert</Link>
          </li>
          <li className="nav-item">
            <Link to="manageAccount" className="nav-link px-3">Manage Account</Link>
          </li>
         
          <li className="nav-item">
            <Link to="addQuizzes" className="nav-link px-3">Add Quizzes</Link>
          </li>
          <li className="nav-item">
            <Link to="/logout" className="nav-link px-3">Logout</Link>
          </li>
        </ul>
      </div>
    </nav>
    <Outlet/>
  </div>
  );
}

