import React, { useState, useEffect } from 'react';

const AttemptQuiz = () => {
    const [quizData, setQuizData] = useState([]);
    const [selectedOptions, setSelectedOptions] = useState([]);
    const [remainingTime, setRemainingTime] = useState(30 * 60); // 30 minutes in seconds
    const [categories, setCategories] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('');
    const [subjects, setSubjects] = useState([]);
    const [selectedSubject, setSelectedSubject] = useState('');

    useEffect(() => {
        fetchCategories();
        fetchQuizData();
        const timer = setInterval(() => {
            setRemainingTime(prevTime => prevTime - 1);
        }, 1000);

        return () => clearInterval(timer);
    }, []);

    useEffect(() => {
        if (selectedCategory) {
            fetchSubjects(selectedCategory);
        }
    }, [selectedCategory]);

    const fetchQuizData = async () => {
        try {
            // Fetch quiz data based on selected category and subject
            // Example URL: `http://localhost:8080/viewQuiz?category=${selectedCategory}&subject=${selectedSubject}`
            const response = await fetch(`http://localhost:8080/viewQuiz?category=${selectedCategory}&subject=${selectedSubject}`);
            if (!response.ok) {
                throw new Error('Failed to fetch quiz data');
            }
            const data = await response.json();
            setQuizData(data);
            setSelectedOptions(Array(data.length).fill(null));
        } catch (error) {
            console.error('Error fetching quiz questions:', error);
        }
    };

    const fetchCategories = async () => {
        try {
            // Fetch categories from Spring API
            const response = await fetch('http://localhost:8080/categories');
            if (!response.ok) {
                throw new Error('Failed to fetch categories');
            }
            const data = await response.json();
            setCategories(data);
        } catch (error) {
            console.error('Error fetching categories:', error);
        }
    };

    const fetchSubjects = async (category) => {
        try {
            // Fetch subjects based on selected category from database
            const response = await fetch(`http://localhost:8080/subjects?category=${category}`);
            if (!response.ok) {
                throw new Error('Failed to fetch subjects');
            }
            const data = await response.json();
            setSubjects(data);
        } catch (error) {
            console.error('Error fetching subjects:', error);
        }
    };

    const handleCategoryChange = (event) => {
        setSelectedCategory(event.target.value);
    };

    const handleSubjectChange = (event) => {
        setSelectedSubject(event.target.value);
    };

    const handleOptionSelect = (questionIndex, optionIndex) => {
        const updatedSelectedOptions = [...selectedOptions];
        updatedSelectedOptions[questionIndex] = optionIndex;
        setSelectedOptions(updatedSelectedOptions);
    };

    const handleSubmitQuiz = () => {
        // Perform submission logic here
        console.log('Submitting quiz...');
        // Show confirmation popup
        if (window.confirm('Are you sure you want to submit the quiz?')) {
            // Submit quiz data
            console.log('Quiz submitted with answers:', selectedOptions);
        }
    };

    const formatTime = (time) => {
        const minutes = Math.floor(time / 60);
        const seconds = time % 60;
        return `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
    };

    return (
        <div>
            <h2>Attempt Quiz</h2>
            <div>
                <label htmlFor="category">Select Category:</label>
                <select id="category" value={selectedCategory} onChange={handleCategoryChange}>
                    <option value="">--Select Category--</option>
                    {categories.map(category => (
                        <option key={category.id} value={category.id}>{category.name}</option>
                    ))}
                </select>
            </div>
            <div>
                <label htmlFor="subject">Select Subject:</label>
                <select id="subject" value={selectedSubject} onChange={handleSubjectChange}>
                    <option value="">--Select Subject--</option>
                    {subjects.map(subject => (
                        <option key={subject.id} value={subject.id}>{subject.name}</option>
                    ))}
                </select>
            </div>
            <p>Remaining Time: {formatTime(remainingTime)}</p>
            {quizData.map((question, index) => (
                <div key={question.id}>
                    <p>{question.question}</p>
                    {question.options.map((option, optionIndex) => (
                        <div key={optionIndex}>
                            <input
                                type="radio"
                                id={`option_${index}_${optionIndex}`}
                                name={`question_${index}`}
                                checked={selectedOptions[index] === optionIndex}
                                onChange={() => handleOptionSelect(index, optionIndex)}
                            />
                            <label htmlFor={`option_${index}_${optionIndex}`}>{option}</label>
                        </div>
                    ))}
                </div>
            ))}
            <button onClick={handleSubmitQuiz}>Submit Quiz</button>
        </div>
    );
};

export default AttemptQuiz;
