import 'bootstrap/dist/css/bootstrap.min.css';

import React, { useReducer } from 'react';
import { Link, useNavigate } from "react-router-dom";



  

function Login() {


  const navigate = useNavigate();

  const init = {
      uname: {value:"",valid: false, touched: false, error:""},
      pwd: {value:"",valid: false, touched: false, error:""},
      formValid: false
  }

  const reducer = (state,action) => {
      switch(action.type)
      {
          case 'update':
              //object destructuring
              const {key,value,touched,valid,error,formValid} = action.data;
              return {...state,[key]:{value,touched,valid,error},formValid}
          case 'reset':
              return init; 
          default:
              
                     
      }
  }

  const[puser,dispatch] = useReducer(reducer,init)


  const validateData = (key,val) => {
      let valid = true;
      let error = ""
      switch(key)
      {
          case 'uname':
             var pattern1 = /^[\w._#-]{4,20}@[\w-]{5,15}\.[a-z]{2,3}$/ 
             if(!pattern1.test(val))
             {
                valid = false;
                error = "invalid email id or unameame"
             }
             break;

          case 'pwd': 
              var pattern2 = /[\w\d@]{3,20}$/ 
             if(!pattern2.test(val))
             {
                valid = false;
                error = "invalid pwdord"
             }
             break;
          default:


      }
      return { valid: valid, error: error}
  }

  
  const handleChange = (key,value) => {
      //1. call validateData function
      const {valid, error} = validateData(key,value);

      //2. check the validity status of the form
      let formValid = true;
      for(let k in puser)
      {
          //console.log(emp[k].valid)
          if(puser[k].valid === false)
          {
              formValid = false;
              break;
          }
      }
      console.log(formValid);

      //3. call to dispatch - updating the state
      dispatch({type: "update",data:{key,value,touched:true,valid,error,formValid}})
  }

  const submitData = (e) =>{
      e.preventDefault();
      //server side API
  }

  


  const InsertData = (e) => {

      e.preventDefault();
      const reOption = {
          method:"POST",
          headers:{'content-type':'application/json'},
          body:JSON.stringify({
              email:puser.uname.value,
              pwdord:puser.pwd.value
              
          })
      }
      
      fetch('http://localhost:8080/checkLogin', reOption)
      .then((response) => {
        if (response.ok) {
          // Successful login
          alert('Login successful!');
  
          // Redirect or perform other actions on successful login
          navigate("/customer-home");
        } else {
          // Login failed
          alert('Login failed. Please enter valid credentials and try again.');
        }
      })  

  }


  return (
    <div className="container">

      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card mt-5">
            <div className="card-body bg-warning">
              <h2 className="text-center mb-4">Login</h2>

              <div className="form-group">
                <input type='text' className="form-control" placeholder='User Name' id="uname" name="uname" autoComplete="off"
                        value={puser.uname.value}
                        onChange={(e)=>{handleChange("uname",e.target.value)}} 
                        onBlur={(e)=>{handleChange("uname",e.target.value)}}
                        />
                      <br/>
                      <div style={{ display: puser.uname.touched && !puser.uname.valid  ?"block":"none",color: 'red' }}>
                      { puser.uname.error}
                      </div>
              </div>

              <br/>

              <div className="form-group">
                <input type='pwdord' className="form-control" placeholder='pwdord' id="pwd" name="pwd" autoComplete="off" 
                    value={puser.pwd.value}
                    onChange={(e)=>{handleChange("pwd",e.target.value)}} 
                    onBlur={(e)=>{handleChange("pwd",e.target.value)}}
                     />

                    <br/>

                    <div style={{ display: puser.pwd.touched && !puser.pwd.valid  ?"block":"none",color: 'red'}}>
                    { puser.pwd.error}
                    </div>
                
              </div>
              <br/>
              <br/>
              <div className="text-center">
                <button type='submit' className="btn btn-primary btn-block btn btn-dark" disabled={!puser.formValid} onClick={(e)=>{InsertData(e)}}>Submit</button>
              </div>
              
              <div className="mt-3">
          <p className="mb-0">
            New Customer? <Link to="/vendor-registration">Register here</Link>
          </p>
          <p className="mb-0">
            New Rest? <Link to="/customer-reg">Register here</Link>
          </p>
        </div>


            </div>
          </div>
        </div>
      </div>

    </div>

  );
}

export default Login;
