import { useReducer, useState } from "react";
import { useNavigate } from "react-router";


const initialState = {
    uid: "",
    pwd: ""
};

const reducer = (state, action) => {
    switch (action.type) {
        case 'update':
            return { ...state, [action.fld]: action.val };
        case 'reset':
            return initialState;
        default:
            return state;
    }
}

function Login() {
    const [user, dispatch] = useReducer(reducer, initialState);
    const [info, setInfo] = useState("");
    const [colour, setColour] = useState({ color: 'red' });
    let navigate = useNavigate();

    const submitData = (e) => {
        e.preventDefault();
        const url= 'http://localhost:8080/checkUser'
        // const params= 
        // {
        //     uname: user.uid,
        //     pwd: user.pwd
        // }
        //const queryString= new URLSearchParams(params).toString();
        const reqOptions = {
            method: "Post",
            headers: { 'content-Type': 'application/json' },
            body: JSON.stringify({
              
                
                uname: user.uid,
                pwd: user.pwd
                
            })
            
        };
    
        fetch(url,reqOptions)
        .then(resp =>{ resp.json()
         console.log(resp.json()) 
        }) // Parse response as JSON
            .then(data => {
                setInfo(data);
                          if(data.role_id === 1)
                            {
                                navigate("/adminHome");
                            }
                            else if (data.role_id === 2)
                            {
                                navigate("/studentHome");
                            }
                            else if (data.role_id === 4)
                            {
                                navigate("/expertHome");
                            }

            })
            .catch(error => {
                console.error("Error fetching data:", error);
                setInfo("An error occurred while logging in.");
            });
    }

    return(
        <div>
            <form>
                <label>Enter uname</label>
                <input 
                    type="text" 
                    name="uid" 
                    value={user.uid}
                    onChange={(e) => dispatch({ type: 'update', fld: 'uid', val: e.target.value })} 
                />
                <br />
                <label>Enter Password</label>
                <input 
                    type="pwd" 
                    name="pwd" 
                    value={user.pwd}
                    onChange={(e) => dispatch({ type: 'update', fld: 'pwd', val: e.target.value })} 
                />
                <br />
                <input 
                    type="submit" 
                    value="Login" 
                    onClick={submitData} 
                />
                <input 
                    type="reset" 
                    value="Clear" 
                    onClick={() => dispatch({ type: "reset" })} 
                />
                {/* <div 
                    style={colour} 
                    onMouseOver={() => setColour({ color: 'blue' })} 
                    onMouseLeave={() => setColour({ color: 'red' })}
                    onClick={() => navigate('/forgotpwd')}
                >
                    Forgot pwd
                </div> */}
            </form>
            <p>{info}</p>
        </div>
    )
    
}
export default Login;
