import { useState } from "react";

export default function Signup() {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [birthDate, setBirthDate] = useState('');
  const [education, setEducation] = useState('');
  const [contact, setContact] = useState('');
  const [email, setEmail] = useState('');
  const [errors, setErrors] = useState({
    firstName: '',
    lastName: '',
    birthDate: '',
    education: '',
    contact: '',
    email: ''
  });

  const validateField = (fieldName, value) => {
    let errorMessage = '';

    switch (fieldName) {
      case 'firstName':
        if (!value.trim()) {
          errorMessage = 'First name is required.';
        }
        break;
      case 'lastName':
        if (!value.trim()) {
          errorMessage = 'Last name is required.';
        }
        break;
      case 'birthDate':
        if (!value.trim()) {
          errorMessage = 'Birth date is required.';
        }
        break;
      case 'education':
        if (!value.trim()) {
          errorMessage = 'Education is required.';
        }
        break;
      case 'contact':
        if (!value.trim()) {
          errorMessage = 'Contact is required.';
        }
        break;
      case 'email':
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(value)) {
          errorMessage = 'Invalid email address.';
        }
        break;
      default:
        break;
    }

    setErrors(prevErrors => ({
      ...prevErrors,
      [fieldName]: errorMessage
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const formValid = Object.values(errors).every(error => error === '');

    if (formValid) {
      console.log('Form submitted:', {
        firstName,
        lastName,
        birthDate,
        education,
        contact,
        email,
      });
    } else {
      console.log('Form contains errors. Please fix them.');
    }
  };

  return (
    <div className="signup-container">
      <h2>Student Registration Form</h2>
      <form className="signup-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label>
            First Name:
            <input
              type="text"
              value={firstName}
              onChange={(e) => {
                setFirstName(e.target.value);
                validateField('firstName', e.target.value);
              }}
            />
            {errors.firstName && <p className="field-error">{errors.firstName}</p>}
          </label>
        </div>
        <div className="form-group">
          <label>
            Last Name:
            <input
              type="text"
              value={lastName}
              onChange={(e) => {
                setLastName(e.target.value);
                validateField('lastName', e.target.value);
              }}
            />
            {errors.lastName && <p className="field-error">{errors.lastName}</p>}
          </label>
        </div>
        <div className="form-group">
          <label>
            Birth Date:
            <input
              type="date"
              value={birthDate}
              onChange={(e) => {
                setBirthDate(e.target.value);
                validateField('birthDate', e.target.value);
              }}
            />
            {errors.birthDate && <p className="field-error">{errors.birthDate}</p>}
          </label>
        </div>
        <div className="form-group">
          <label>
            Education:
            <input
              type="text"
              value={education}
              onChange={(e) => {
                setEducation(e.target.value);
                validateField('education', e.target.value);
              }}
            />
            {errors.education && <p className="field-error">{errors.education}</p>}
          </label>
        </div>
        <div className="form-group">
          <label>
            Contact:
            <input
              type="text"
              value={contact}
              onChange={(e) => {
                setContact(e.target.value);
                validateField('contact', e.target.value);
              }}
            />
            {errors.contact && <p className="field-error">{errors.contact}</p>}
          </label>
        </div>
        <div className="form-group">
          <label>
            Email:
            <input
              type="email"
              value={email}
              onChange={(e) => {
                setEmail(e.target.value);
                validateField('email', e.target.value);
              }}
            />
            {errors.email && <p className="field-error">{errors.email}</p>}
          </label>
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
